-- Dumping data for table laravel_blogs.categories: ~4 rows (approximately)
INSERT INTO `categories` (`id`, `title`, `short_desc`, `created_at`, `updated_at`) VALUES
	(1, 'Kiếm tiền online', 'Kiếm tiền online là tên gọi Việt hóa của MMO (Make Money Online) - một thuật ngữ kinh doanh hiện đại dùng để chỉ hình thức lao động thông qua nền tảng số.', '2023-08-03 10:20:09', '2023-08-03 12:08:24'),
	(2, 'Content Marketing', '<p>Content Marketing (hay tiếp thị nội dung) l&agrave; một c&aacute;ch tiếp thị tập trung v&agrave;o việc tạo dựng v&agrave; ph&acirc;n phối nội dung c&oacute; gi&aacute; trị, ph&ugrave; hợp v&agrave; đồng nhất đến với kh&aacute;ch h&agrave;ng. Th&ocirc;ng qua h&igrave;nh thức marketing n&agrave;y, người d&ugrave;ng sẽ nhận được lợi &iacute;ch từ đ&oacute; v&agrave; muốn mua sản phẩm, dịch vụ để thực sự trải nghiệm ch&uacute;ng.</p>', '2023-08-03 17:26:00', '2023-08-04 04:08:58'),
	(3, 'Facebook Ads', '<p>Facebook Ads (viết tắt của Facebook Advertising) l&agrave; một dịch vụ quảng c&aacute;o của Facebook. Đ&acirc;y l&agrave; dạng quảng c&aacute;o được trả ph&iacute; để hiển thị những chương tr&igrave;nh khuyến m&atilde;i, ưu đ&atilde;i hoặc sản phẩm đến những đối tượng kh&aacute;ch h&agrave;ng tiềm năng tr&ecirc;n Facebook v&agrave; những mạng x&atilde; hội li&ecirc;n kết với Facebook.</p>', '2023-08-03 17:26:18', '2023-08-04 04:07:52'),
	(4, 'Google Ads', 'Google Ads là chương trình quảng cáo trực tuyến của Google. Thông qua Google Ads, bạn có thể tạo quảng cáo trực tuyến để tiếp cận chính xác những người quan tâm đến các sản phẩm và dịch vụ mà bạn cung cấp.', '2023-08-03 12:09:47', '2023-08-03 12:09:47');

